import React, { Component } from "react";

class Hand extends Component {
  constructor(props) {
    super(props);
    this.state = {
      holeCards: [],
      flop: [],
      turn: [],
      river: [],
      values: ["2", "3", "4", "5", "6", "7", "8", "9", "0", "J", "Q", "K", "A"],
    };
  }

  handName = (holeCards, flop, turn, river) => {
    if (flop.length === 0) return this.evaluateHoleCards(holeCards);
    else if (turn.length === 0) return this.evaluateFlop(holeCards, flop);
    else if (river.length === 0)
      return this.evaluateTurn(holeCards, flop, turn);
    else return this.evaluateRiver(holeCards, flop, turn, river);
  };

  evaluateHoleCards = (holeCards) => {
    if (holeCards[0].substr(0, 1) === holeCards[1].substr(0, 1))
      return "Pair of " + holeCards[0].substr(0, 1);
    else {
      if (
        this.state.values.indexOf(holeCards[0].substr(0, 1)) >
        this.state.values.indexOf(holeCards[1].substr(0, 1))
      ) {
        return holeCards[0].substr(0, 1) + " high";
      } else return holeCards[1].substr(0, 1) + " high";
    }
  };

  evaluateFlop = (holeCards, flop) => {};

  evaluateTurn = (holeCards, flop, turn) => {};

  evaluateRiver = (holeCards, flop, turn, river) => {};

  render() {
    const { dealHoleCards, holeCards, flop, turn, river } = this.props;
    return (
      <React.Fragment>
        <span>{dealHoleCards ? <p>{this.handName()}</p> : null}</span>
      </React.Fragment>
    );
  }
}

export default Hand;
