import React, { Component } from "react";

class PlayerBanner extends Component {
  state = {
    name: "Type your name here.",
    stackSize: 0,
  };
  render() {
    return (
      <React.Fragment>
        <input
          type="text"
          id="name"
          name="name"
          placeholder={this.state.name}
        ></input>
        <span>{this.state.stackSize}</span>
      </React.Fragment>
    );
  }
}

export default PlayerBanner;
