var values = ["2", "3", "4", "5", "6", "7", "8", "9", "0", "J", "Q", "K", "A"];
var rank = [
  "High card",
  "Pair",
  "Two pair",
  "Three of a kind",
  "Straight",
  "Flush",
  "Full House",
  "Four of a kind",
  "Straight Flush",
  "Royal Flush",
];

export const evaluateHoleCards = (holeCards) => {
  var card1 = holeCards[0].code;
  var card2 = holeCards[1].code;
  if (card1.substring(0, 1) === card2.substring(0, 1)) return "Pair";
  else {
    return "High card";
  }
};
